import React, { useState, useEffect } from "react";
import "../Style.css";
import Logo from "./Logo.js";
import SearchPage from "./SearchPage.js";
import End from "./End.js";
import { FaBus } from "react-icons/fa";
import { GiKickScooter } from "react-icons/gi";
import { BsArrowRight } from "react-icons/bs";


//import 'webpack';
/* import logo from '.../public/busemoji.png'

console.log(logo); // /logo.84287d09.png

function Header() {
  // Import result is the URL of your image
  return <img src={logo} alt="Logo" />;
}
 */

const ConnectionDisplay = (props) => {
  const [clickedEnd, setEndClicked] = useState(false);
  const [hideConnectionDisplay, setHideConnectionDisplay] = useState(true);
  const [loadingMessage, setloadingMessage] = useState(true);

  const backClickHandler = () => {
    props.onGoBack();
  };
  const endClickHandler = () => {
    setEndClicked(true);
  };
// -------------------------------WICHTIGE FERTIGE NOTIZEN FÜRT DIE VERZÖGERUNG---------------------------------------------------------------
  // für die verzögerung: use effect importieren
  //todo: dann noch loading bar hinzufügen
  useEffect(() => {
    const timer = setTimeout(() => {
   setHideConnectionDisplay(false);
   setloadingMessage(false);
    }, 1000);
    return () => clearTimeout(timer);
  }, []);

  // if(loadingMessage){<p>...loading...</p>};
//   --------------------------(nicht ganz so wichtige) Ideen für Eingabezeit + paar Sekunden als Losfahrzeit anzeigen--------------------


// zu einer Zeit die props durationH und Min hinzufügen
const addDuration = (time) => {
  console.log("time =" + time );
  const [hours, minutes] = time.split(":");
  let hours3 = Number(hours);
    //  console.log("hours3  = " + hours3 );
    // console.log("hours von eingabe (newDeparturetime) = " + hours );
    // console.log("Number(hours)von eingabe (newDeparturetime) =" + Number(hours) );
    // console.log("props.durationH = " + props.durationH );
    // console.log("Number(props.durationH) = " + Number(props.durationH) );
    // console.log("minutes  von eingabe (newDeparturetime) = " + minutes);
    // console.log("Number(minutes)von eingabe (newDeparturetime) =" + Number(minutes) );
    // console.log("props.durationMin = " + props.durationMin );
    // console.log("Number(props.durationMin) = " + Number(props.durationMin) );
  let hours2 = Number(hours) + Number(props.durationH);
  let minutes2 = Number(minutes) + Number(props.durationMin); 
    // console.log("hours2 (von eingabe (newDeparturetime) + props.durationH)= " + hours2 );
    // console.log("minutes2 (von eingabe (newDeparturetime) + props.durationMin)= " + minutes2 );
    // console.log(hours2 + ":" + minutes2 );
  while(minutes>=60) {
     minutes = minutes -60;
    hours = hours+1 ;}
  return(hours2 + ":" + minutes2 );
}

// zum in-string konvertieren, außerdem zwischen 3-9 min (zufällig) hinzufügen 
const timeToDecimalAdd = (time) => {
  const [hours, minutes] = time.split(":");
  let additionalRandomMinutes =  props. additionalRandomMinutes;
  let tneu=Number(minutes)+ Number(additionalRandomMinutes);
  return(Number(hours) + Number(tneu) / 60 );
}

//   zum String in Zeit konvertieren 
const stringToTime = (StringTime) => {
  const hs = Math.floor(StringTime);
  const mins = Math.round((StringTime - hs) * 60);
  return `${hs < 10 ? "0" : ""}${hs}:${mins < 10 ? "0" : ""}${mins}`;
}

let newDepartureTime = stringToTime(timeToDecimalAdd(props.departureTime));

  return (
    <div>
    { props.cheaperConnectionStarted ? (
    <div>
      {clickedEnd ? (
        <End 
        // onGoBack={() => {
        // setHideConnectionDisplay(false);
        // setEndClicked(false);}
        />
      ) : ( 
        <div className="container-verbindung">
          {/* {endClickHandler ? null:  */}
